var LoginHelper = { SetCookie: function (e, t) { var n = new Date; var r = 30; n.setTime(n.getTime() + r * 60 * 1e3); $.cookie(e, t, { expires: n, path: "/" }) }, ValidateLogin: function (e, t) { $.ajax({ url: WCFServiceURL + "Login", method: "get", dataType: "json", data: "username=" + e + "&password=" + t, success: function (t) { if (t.status == "success") { if (t.message == "Un-Authorized") alert("Invalid username/password"); else { LoginHelper.SetCookie("token", t.data.token); LoginHelper.SetCookie("user", e); document.location.href = "Monitoring/index4.html" } } }, error: function (e) { alert("There is an error occured while login.") }, complete: function () { } }) } }